import { publicAssetsURL } from '#internal/nuxt/paths';export default publicAssetsURL("/images/ns-logo.png")
<script setup lang="ts">
const links = [
  { label: 'ABOUT', to: { path: '/', hash: '#about' } },
  { label: 'PROGRAM', to: { path: '/', hash: '#program' } },
  { label: 'FAQ', to: { path: '/', hash: '#faq' } }
]
</script>

<template>
  <header class="header">
    <div class="container bar">
      <NuxtLink class="brand" to="/" aria-label="Home">
        <img class="logo" src="/images/ns-logo.png" alt="CK logo" width="34" height="34" />
        <span class="brandText">Chak &amp; Kez</span>
      </NuxtLink>

      <nav class="nav" aria-label="Primary">
        <NuxtLink v-for="l in links" :key="l.label" class="navLink" :to="l.to">
          {{ l.label }}
        </NuxtLink>
      </nav>

      <NuxtLink class="btn rsvp" to="/rsvp">
        RSVP
        <span class="arrow" aria-hidden="true">↗</span>
      </NuxtLink>
    </div>
  </header>
</template>

<style scoped>
.header{
  position: sticky;
  top: 0;
  z-index: 20;
  background: rgba(222, 220, 215, .85);
  backdrop-filter: blur(10px);
  border-bottom: 1px solid rgba(0,0,0,.08);
}
.bar{
  height: 72px;
  display: grid;
  grid-template-columns: auto 1fr auto;
  align-items: center;
  gap: 16px;
}
.brand{
  display: inline-flex;
  align-items: center;
  gap: 10px;
  text-decoration: none;
}
.logo{
  border-radius: 10px;
}
.brandText{
  font-weight: 700;
  letter-spacing: -0.01em;
}
.nav{
  display: flex;
  align-items: center;
  gap: 14px;
  justify-content: center;
}
.navLink{
  font-size: 12px;
  letter-spacing: .18em;
  text-transform: uppercase;
  color: rgba(0,0,0,.72);
  text-decoration: none;
}
.navLink:hover{ text-decoration: underline; }

@media (max-width: 700px){
  .navLink{ display: none; }
  .nav{ justify-content: end; }
  .brandText{ display: none; }
}

.rsvp{
  border-radius: 0;
  padding: 10px 14px;
}
.arrow{ opacity: .9; }
</style>
<script setup lang="ts">
const links = [
  { label: 'ABOUT', to: { path: '/', hash: '#about' } },
  { label: 'PROGRAM', to: { path: '/', hash: '#program' } },
  { label: 'FAQ', to: { path: '/', hash: '#faq' } },
  { label: 'RSVP', to: '/rsvp' }
]
</script>

<template>
  <footer class="footer">
    <div class="container inner">
      <div class="brand">
        <img class="logo" src="/images/ns-logo.png" alt="NS logo" width="44" height="44" />
        <div>
          <div class="title">Noah &amp; Sabrina</div>
          <div class="copy">
            We are so excited to celebrate our love with you! Now, we’re stepping into our next chapter, hand in
            hand, with hearts full of gratitude for the love that surrounds us.
          </div>
        </div>
      </div>

      <nav class="nav" aria-label="Footer">
        <NuxtLink v-for="l in links" :key="l.label" class="navLink" :to="l.to">
          {{ l.label }}
        </NuxtLink>
      </nav>
    </div>
  </footer>
</template>

<style scoped>
.footer{
  padding: 54px 0;
  border-top: 1px solid rgba(0,0,0,.08);
}
.inner{
  display: grid;
  grid-template-columns: 1fr auto;
  gap: 22px;
  align-items: start;
}
.brand{
  display: grid;
  grid-template-columns: auto 1fr;
  gap: 14px;
  align-items: start;
}
.logo{ border-radius: 14px; }
.title{ font-weight: 800; }
.copy{ margin-top: 6px; color: rgba(0,0,0,.7); max-width: 62ch; }
.nav{
  display: flex;
  flex-wrap: wrap;
  gap: 12px 14px;
  justify-content: flex-end;
}
.navLink{
  font-size: 12px;
  letter-spacing: .18em;
  text-transform: uppercase;
  text-decoration: none;
  color: rgba(0,0,0,.72);
}
.navLink:hover{ text-decoration: underline; }

@media (max-width: 800px){
  .inner{ grid-template-columns: 1fr; }
  .nav{ justify-content: flex-start; }
}
</style>
<template>
  <div class="layout">
    <SiteHeader />

    <main>
      <slot />
    </main>

    <SiteFooter />
  </div>
</template>

<style scoped>
.layout{
  min-height: 100dvh;
  display: flex;
  flex-direction: column;
}
main{
  flex: 1;
}
</style>
