import { publicAssetsURL } from '#internal/nuxt/paths';export default publicAssetsURL("/images/hero.png")
<template>
  <section class="hero">
    <div class="container">
      <div class="inner">
        <h1 class="title">CHAK &amp; KEZIAH</h1>

        <div class="meta" aria-label="Location and date">
          <span class="metaText">ACCRA - GHANA</span>
          <span class="rule" aria-hidden="true" />
          <span class="icon" aria-hidden="true">
            <svg viewBox="0 0 24 24" width="16" height="16" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path
                d="M12 22a2.2 2.2 0 0 0 2.2-2.2H9.8A2.2 2.2 0 0 0 12 22Z"
                fill="currentColor"
                opacity=".9"
              />
              <path
                d="M18 16.7H6c1.2-1.2 1.9-2.7 1.9-4.5V9.5a4.1 4.1 0 0 1 3.1-4V4.2a1 1 0 1 1 2 0v1.3a4.1 4.1 0 0 1 3.1 4v2.7c0 1.8.7 3.3 1.9 4.5Z"
                stroke="currentColor"
                stroke-width="1.6"
                stroke-linecap="round"
                stroke-linejoin="round"
              />
            </svg>
          </span>
          <span class="metaText">SATURDAY, AUGUST 8TH</span>
        </div>
      </div>
    </div>

    <div class="media">
      <img
        class="heroImg"
        src="/images/hero.png"
        alt="A bride and groom hold hands tenderly."
        width="1600"
        height="760"
        loading="eager"
      />
    </div>
  </section>
</template>

<style scoped>
.hero{
  padding-top: 86px;
}

.inner{
  padding: 60px 0 46px;
  text-align: center;
}

.title{
  margin: 0;
  font-family: var(--serif);
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: -0.02em;
  line-height: .92;
  font-size: clamp(56px, 9.2vw, 150px);
}

.meta{
  margin-top: 22px;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 12px;
  color: rgba(0,0,0,.72);
}

.metaText{
  font-size: 12px;
  letter-spacing: .18em;
  text-transform: uppercase;
  white-space: nowrap;
}

.rule{
  width: min(260px, 22vw);
  height: 1px;
  background: rgba(0,0,0,.35);
}

.icon{
  display: inline-flex;
  align-items: center;
  justify-content: center;
  color: rgba(0,0,0,.65);
}

.media{
  width: 100vw;
  margin-left: calc(50% - 50vw);
  margin-right: calc(50% - 50vw);
}

.heroImg{
  display: block;
  width: 100%;
  height: clamp(320px, 64vh, 560px);
  object-fit: cover;
}

@media (max-width: 520px){
  .inner{ padding: 34px 0 28px; }
  .meta{ gap: 10px; }
  .rule{ width: 16vw; }
}
</style>
<script setup lang="ts">
import { computed } from 'vue'

const images = [
  { src: '/images/hero.png', alt: 'Wedding photo' },
  { src: '/images/rsvp.jpg', alt: 'Wedding photo' },
  { src: '/images/hero.png', alt: 'Wedding photo' },
  { src: '/images/rsvp.jpg', alt: 'Wedding photo' }
]

const trackImages = computed(() => [...images, ...images])
</script>

<template>
  <section class="section thanks">
    <div class="container">
      <div class="content">
        <img class="monogram" src="/images/ns-logo.png" alt="NS monogram" loading="lazy" />

        <p class="message">
          We are thrilled to have you on our special day,<br />
          and we are honoured that you have travelled to be here with us.
        </p>

        <div class="signature">
          <div class="withLove">with love,</div>
          <div class="names">Chak &amp; Keziah</div>
        </div>
      </div>
    </div>

    <div class="gallery" aria-label="Gallery">
      <div class="viewport">
        <div class="track" role="list">
          <figure
            v-for="(img, idx) in trackImages"
            :key="`${img.src}-${idx}`"
            class="tile"
            role="listitem"
            :aria-hidden="idx >= images.length"
          >
            <img :src="img.src" :alt="idx < images.length ? img.alt : ''" loading="lazy" />
          </figure>
        </div>
      </div>
    </div>
  </section>
</template>

<style scoped>
.thanks{
  padding-top: 84px;
  padding-bottom: 0;
}

.content{
  max-width: 720px;
  margin: 0 auto;
  text-align: center;
  display: grid;
  gap: 22px;
  justify-items: center;
}

.monogram{
  width: 120px;
  height: auto;
  opacity: 0.95;
}

.message{
  margin: 0;
  font-family: var(--sans);
  font-size: 16px;
  color: rgba(0,0,0,.78);
  letter-spacing: 0.01em;
  line-height: 1.75;
}

.signature{
  display: grid;
  gap: 6px;
  padding-top: 10px;
  padding-bottom: 48px;
}

.withLove{
  font-family: var(--serif);
  font-style: italic;
  color: rgba(0,0,0,.66);
}

.names{
  font-family: var(--serif);
  font-size: 26px;
  letter-spacing: -0.02em;
}

.gallery{
  width: 100%;
  padding: 0 20px 28px;
}

.viewport{
  max-width: 1320px;
  margin: 0 auto;
  overflow: hidden;
  position: relative;
  -webkit-mask-image: linear-gradient(to right, transparent, black 8%, black 92%, transparent);
  mask-image: linear-gradient(to right, transparent, black 8%, black 92%, transparent);
}

.track{
  display: flex;
  gap: 14px;
  width: max-content;
  animation: scroll 34s linear infinite;
  will-change: transform;
}

@keyframes scroll{
  from{ transform: translateX(0); }
  to{ transform: translateX(-50%); }
}

.tile{
  margin: 0;
  border-radius: 10px;
  overflow: hidden;
  background: rgba(255,255,255,.35);
  border: 1px solid rgba(0,0,0,.10);
}

.tile img{
  width: 100%;
  height: 260px;
  object-fit: cover;
  display: block;
}

@media (max-width: 900px){
  .thanks{ padding-top: 64px; }
  .track{ animation-duration: 28s; }
  .tile img{ height: 220px; }
}

@media (max-width: 520px){
  .message{ font-size: 15px; }
  .names{ font-size: 22px; }
  .tile img{ height: 180px; }
}

@media (prefers-reduced-motion: reduce){
  .track{ animation: none; }
  .viewport{ -webkit-mask-image: none; mask-image: none; }
}
</style>
<template>
  <section id="about" class="section">
    <div class="container">
      <div class="top">
        <p class="line left">We met by chance.</p>
        <p class="line center">We stayed by choice.</p>
        <p class="line right">We love for life.</p>
      </div>

      <div class="content">
        <div class="text">
          <p>
            Noah and Sabrina met on a spontaneous backpacking trip in Barcelona, and life’s been a joyful adventure
            ever since. From hunting down new foods and hiking trails, to getting delightfully lost in a tiny Gothic
            Quarter bookshop, we’ve always known we were a perfect match.
          </p>
          <p>
            Noah popped the question under the magical glow of Montjuïc’s fountain with a surprise picnic and
            serenade.
          </p>
          <p>
            Now, we’re excited to celebrate this new chapter with all of you!
          </p>
        </div>

        <figure class="media" aria-label="A photo of the couple">
          <img
            class="photo"
            src="/images/hero.png"
            alt=""
            loading="lazy"
            decoding="async"
          />
        </figure>
      </div>
    </div>
  </section>
</template>

<style scoped>
.top{
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  gap: 18px;
  margin: 0 auto 52px;
}

.line{
  margin: 0;
  font-family: var(--serif);
  font-weight: 500;
  font-size: clamp(22px, 2.8vw, 40px);
  letter-spacing: -0.02em;
  line-height: 1.15;
}

.left{ text-align: left; }
.center{ text-align: center; }
.right{ text-align: right; }

.content{
  display: grid;
  grid-template-columns: 1fr 1.85fr;
  align-items: start;
  gap: 40px;
}

.text{
  max-width: 46ch;
  color: rgba(0,0,0,.78);
  font-size: 16px;
  line-height: 1.75;
}

.text p{ margin: 0 0 22px; }
.text p:last-child{ margin-bottom: 0; }

.media{
  margin: 0;
  border-radius: 16px;
  overflow: hidden;
  background: rgba(0,0,0,.06);
}

.photo{
  width: 100%;
  height: auto;
  display: block;
  aspect-ratio: 16 / 9;
  object-fit: cover;
}

@media (max-width: 900px){
  .content{ grid-template-columns: 1fr; }
  .text{ max-width: 62ch; }
  .top{ grid-template-columns: 1fr; gap: 10px; margin-bottom: 34px; }
  .left, .center, .right{ text-align: left; }
}
</style>
<script setup lang="ts">
type ProgramItem = {
  title: string
  description: string
  date: string
  time: string
  location: string
  dressCode: string
  imageSrc: string
  imageAlt: string
}

const items: ProgramItem[] = [
  {
    title: 'Pre-Wedding Gathering',
    description:
      "Before the wedding begins, we would love to welcome our guests to a relaxed night together in the city. It is a wonderful chance to catch up, and enjoy Barcelona’s atmosphere.",
    date: 'Friday, September 26, 2026',
    time: '7:00 PM - 10:00 PM',
    location: 'Bar del Pla in El Born',
    dressCode: 'Smart Casual',
    imageSrc: '/images/rsvp.jpg',
    imageAlt: 'Outdoor dinner setting'
  },
  {
    title: 'Wedding Celebrations',
    description:
      'Our celebration takes place at Castell de Sant Marçal, a historic 11th-century castle with gardens, a lake, and architecture that blend grandeur with elegance.',
    date: 'Saturday, September 27, 2026',
    time: '4:00 PM (Ceremony start time)',
    location: 'Castell de Sant Marçal',
    dressCode: 'Semi-Formal / Cocktail Attire',
    imageSrc: '/images/hero.png',
    imageAlt: 'Wedding ceremony setting'
  }
]
</script>

<template>
  <section id="program" class="section">
    <div class="container">
      <div class="head">
        <h2 class="sectionTitle">Wedding Program</h2>
      </div>

      <div class="stack">
        <article
          v-for="(item, idx) in items"
          :key="item.title"
          class="programCard"
          :class="{ reverse: idx % 2 === 1 }"
        >
          <div class="panel">
            <h3 class="cardTitle">{{ item.title }}</h3>
            <p class="cardDesc">{{ item.description }}</p>

            <div class="divider" />

            <dl class="details">
              <div class="detailRow">
                <dt>Date</dt>
                <dd>{{ item.date }}</dd>
              </div>
              <div class="detailRow">
                <dt>Time</dt>
                <dd>{{ item.time }}</dd>
              </div>
              <div class="detailRow">
                <dt>Location</dt>
                <dd>{{ item.location }}</dd>
              </div>
              <div class="detailRow">
                <dt>Dress Code</dt>
                <dd>{{ item.dressCode }}</dd>
              </div>
            </dl>
          </div>

          <div class="media">
            <img
              class="photo"
              :src="item.imageSrc"
              :alt="item.imageAlt"
              loading="lazy"
              decoding="async"
            />
          </div>
        </article>
      </div>
    </div>
  </section>
</template>

<style scoped>
.head{
  text-align: center;
  margin-bottom: 44px;
}

.sectionTitle{
  margin: 0;
  font-family: var(--serif);
  font-weight: 500;
  font-size: clamp(34px, 4vw, 54px);
  letter-spacing: -0.02em;
}

.stack{
  display: grid;
  gap: 28px;
}

.programCard{
  display: grid;
  grid-template-columns: 1fr 1.6fr;
  border-radius: 16px;
  overflow: hidden;
  background: var(--panel);
  border: 1px solid rgba(0,0,0,.08);
}

.panel{
  padding: 42px 44px;
}

.cardTitle{
  margin: 0 0 14px;
  font-family: var(--serif);
  font-weight: 500;
  font-size: clamp(24px, 2.8vw, 34px);
  letter-spacing: -0.02em;
}

.cardDesc{
  margin: 0;
  max-width: 54ch;
  color: rgba(0,0,0,.72);
  line-height: 1.75;
}

.divider{
  margin: 28px 0 14px;
  height: 1px;
  background: rgba(0,0,0,.08);
}

.details{ margin: 0; }

.detailRow{
  display: grid;
  grid-template-columns: 120px 1fr;
  align-items: center;
  gap: 20px;
  padding: 22px 0;
  border-top: 1px solid rgba(0,0,0,.06);
}

.detailRow:first-child{ border-top: 0; }

.detailRow dt{
  color: rgba(0,0,0,.6);
}

.detailRow dd{
  margin: 0;
  text-align: right;
  font-family: var(--serif);
  font-weight: 500;
  font-size: 20px;
  letter-spacing: -0.01em;
  color: rgba(0,0,0,.86);
}

.media{
  background: rgba(0,0,0,.06);
}

.photo{
  width: 100%;
  height: 100%;
  display: block;
  object-fit: cover;
  min-height: 420px;
}

.reverse .panel{ order: 2; }
.reverse .media{ order: 1; }

@media (max-width: 980px){
  .programCard{ grid-template-columns: 1fr; }
  .panel{ padding: 30px 22px; }
  .detailRow{ grid-template-columns: 110px 1fr; }
  .detailRow dd{ font-size: 18px; }
  .photo{ min-height: 300px; }
  .reverse .panel, .reverse .media{ order: initial; }
}
</style>
<script setup lang="ts">
const items = [
  {
    q: 'Can we take photos during the wedding?',
    a: 'Yes—please feel free to take photos. We’d love it if you shared your favorites with us after the weekend.'
  },
  {
    q: 'Can I bring my children to the wedding?',
    a: 'To help everyone fully enjoy the celebration, the ceremony and reception will be adults-only. Children are welcome to join us for the Farewell Brunch.'
  },
  {
    q: 'How can we honor you with a gift?',
    a: 'Your presence is the greatest gift. If you’d still like to give something, a honeymoon contribution is sincerely appreciated.'
  },
  {
    q: 'What if I have dietary restrictions or allergies?',
    a: 'Please note any allergies or dietary restrictions in your RSVP. We will do our best to accommodate.'
  },
  {
    q: 'Do I need to bring a physical invitation?',
    a: 'No—your name will be on our guest list. If you have questions about your invite, reach out and we’ll confirm details.'
  },
  {
    q: 'Who should I reach out to if I need support?',
    a: 'Send us a note through the RSVP page and we’ll get back to you as soon as possible.'
  }
]
</script>

<template>
  <section id="faq" class="section">
    <div class="container">
      <div class="head">
        <div class="kicker">FAQ</div>
        <h2 class="title">Everything you need to know</h2>
      </div>

      <div class="list">
        <details v-for="item in items" :key="item.q" class="item">
          <summary class="summary">
            <span class="question">{{ item.q }}</span>
            <span class="plus" aria-hidden="true" />
          </summary>
          <div class="answer">{{ item.a }}</div>
        </details>
      </div>
    </div>
  </section>
</template>

<style scoped>
.head{
  text-align: center;
}

.title{
  margin: 0;
  font-family: var(--serif);
  font-weight: 500;
  font-size: clamp(34px, 4vw, 54px);
  letter-spacing: -0.02em;
}

.list{
  max-width: 980px;
  margin: 44px auto 0;
  border-top: 1px solid rgba(0,0,0,.08);
}

.item{
  padding: 0;
  border-bottom: 1px solid rgba(0,0,0,.08);
}

.summary{
  list-style: none;
  cursor: pointer;
  padding: 26px 0;
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 18px;
}

.summary::-webkit-details-marker{ display: none; }

.question{
  font-weight: 650;
  letter-spacing: -0.01em;
}

.plus{
  position: relative;
  width: 18px;
  height: 18px;
  flex: 0 0 18px;
  opacity: 0.85;
}

.plus::before,
.plus::after{
  content: '';
  position: absolute;
  left: 50%;
  top: 50%;
  background: rgba(0,0,0,.7);
  transform: translate(-50%, -50%);
  transition: transform 160ms ease, opacity 160ms ease;
}

.plus::before{ width: 14px; height: 2px; }
.plus::after{ width: 2px; height: 14px; }

details[open] .plus::after{
  opacity: 0;
  transform: translate(-50%, -50%) scaleY(0.3);
}

.answer{
  padding: 0 0 22px;
  max-width: 820px;
  color: rgba(0,0,0,.75);
}

@media (max-width: 800px){
  .summary{ padding: 22px 0; }
}
</style>
<template>
  <section class="section">
    <div class="container">
      <div class="wrap">
        <h2 class="headline">
          Your presence would mean the world to<br />
          us on our big day.
        </h2>

        <p class="copy">Kindly reply by <span class="date">AUGUST 8th, 2026</span>.</p>

        <NuxtLink class="cta" to="/rsvp">
          <span>RSVP</span>
          <span class="arrow" aria-hidden="true">↗</span>
        </NuxtLink>
      </div>
    </div>
  </section>
</template>

<style scoped>
.wrap{
  text-align: center;
  max-width: 980px;
  margin: 0 auto;
  display: grid;
  justify-items: center;
  gap: 18px;
}

.headline{
  margin: 0;
  font-family: var(--serif);
  font-weight: 300;
  font-size: clamp(36px, 5vw, 72px);
  line-height: 1.05;
  letter-spacing: -0.02em;
}

.copy{
  margin: 0;
  color: rgba(0,0,0,.75);
  letter-spacing: -0.01em;
}

.date{ font-weight: 700; letter-spacing: 0.04em; }

.cta{
  width: min(240px, 100%);
  display: inline-flex;
  align-items: center;
  justify-content: space-between;
  gap: 16px;
  padding: 16px 18px;
  border-radius: 0;
  border: 1px solid rgba(0,0,0,.35);
  background: transparent;
  color: var(--text);
  font-weight: 600;
  letter-spacing: 0.08em;
  text-transform: uppercase;
}

.cta:hover{ text-decoration: none; filter: brightness(0.98); }

.arrow{
  font-size: 18px;
  line-height: 1;
}

@media (max-width: 600px){
  .headline br{ display: none; }
}
</style>
<script setup lang="ts">
useHead({
  title: 'Chak & Kez'
})
</script>

<template>
  <div>
    <SectionHero />
    <SectionThanks />
    <SectionAbout />
    <SectionProgram />
    <SectionFaq />
    <SectionRsvpCta />
  </div>
</template>
